import React, { useEffect, useState } from "react";

const UserPanel = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  // Fetch products from the API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/products");
        const data = await response.json();
        if (response.ok) {
          setProducts(data);
        } else {
          setError(data.message || "Error fetching products");
        }
      } catch (err) {
        setError("An error occurred while fetching products");
      }
    };

    fetchProducts();
  }, []);

  // Handle toggle for "More Info"
  const toggleMoreInfo = (index) => {
    const updatedProducts = [...products];
    updatedProducts[index].showMoreInfo = !updatedProducts[index].showMoreInfo;
    setProducts(updatedProducts);
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
        Product Catalog
      </h2>
      {error && <div className="text-red-500 text-center mb-4">{error}</div>}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {products.map((product, index) => (
          <div
            key={product._id}
            className="bg-white border border-gray-200 rounded-lg shadow p-4"
          >
            <h3 className="text-lg font-semibold text-gray-800">
              {product.name}
            </h3>
            <p className="text-gray-600">Price: ${product.price}</p>
            <p className="text-gray-600">Category: {product.category}</p>
            <p className="text-gray-600">Stock: {product.stock}</p>
            {product.showMoreInfo && (
              <div className="mt-2 text-gray-700">
                <strong>Description:</strong> {product.description}
              </div>
            )}
            <button
              onClick={() => toggleMoreInfo(index)}
              className="mt-4 w-full text-sm text-indigo-600 font-medium hover:underline"
            >
              {product.showMoreInfo ? "Show Less" : "More Info"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserPanel;
