import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";

const UserPanel = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  // Fetch products from the API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/products");
        const data = await response.json();
        if (response.ok) {
          setProducts(data);
        } else {
          setError(data.message || "Error fetching products");
        }
      } catch (err) {
        setError("An error occurred while fetching products");
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (productId) => {
    alert(`Product ${productId} added to cart!`);
    // Add functionality to send the product to the backend/cart here
  };

  return (
    <>
      <Navbar />
      <div className="bg-gray-50 min-h-screen">
        <div className="container mx-auto py-8 px-4">
          <h2 className="text-3xl font-bold text-green-700 text-center mb-8">
            Explore Our Products
          </h2>
          {error && (
            <div className="text-red-500 text-center mb-4">{error}</div>
          )}

          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {products.map((product) => (
              <div
                key={product._id}
                className="bg-white border border-gray-200 rounded-lg shadow-sm hover:shadow-md transition p-4"
              >
                <img
                  src={product.img}
                  alt={product.name}
                  className="w-full h-48 object-cover rounded-md mb-4"
                />
                <h3 className="text-xl font-semibold text-gray-800">
                  {product.name}
                </h3>
                <p className="text-gray-600 text-sm mb-2">
                  Category: {product.category}
                </p>
                <p className="text-gray-600 mb-4">Price: Rs.{product.price}</p>
                <p className="text-gray-600 text-sm mb-4 truncate">
                  {product.description}
                </p>
                <button
                  onClick={() => addToCart(product._id)}
                  className="w-full py-2 bg-green-600 text-white font-medium rounded-md shadow hover:bg-green-700 transition focus:outline-none focus:ring-2 focus:ring-green-400"
                >
                  Add to Cart
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default UserPanel;
