import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "./Navbar";
import "./UserPanel.css"; // Import a separate CSS file for styles

const UserPanel = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);
  const [isBlurry, setIsBlurry] = useState(false);
  const [cart, setCart] = useState(() => {
    const savedCart = localStorage.getItem("cart");
    return savedCart ? JSON.parse(savedCart) : [];
  });
  const navigate = useNavigate();

  // Fetch products from the API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/products");
        const data = await response.json();
        if (response.ok) {
          setProducts(data.map((product) => ({ ...product, expanded: false })));
        } else {
          setError(data.message || "Error fetching products");
        }
      } catch (err) {
        setError("An error occurred while fetching products");
      }
    };

    fetchProducts();
  }, []);

  // Handle card toggle
  const toggleCard = (index) => {
    setProducts((prevProducts) =>
      prevProducts.map((product, i) => {
        if (i === index) {
          return { ...product, expanded: !product.expanded };
        }
        return { ...product, expanded: false };
      })
    );
    setIsBlurry((prev) => !prev);
  };

  // Collapse all cards when clicking outside
  const collapseAll = () => {
    setProducts((prevProducts) =>
      prevProducts.map((product) => ({ ...product, expanded: false }))
    );
    setIsBlurry(false);
  };

  // Add product to cart
  const addToCart = (productId) => {
    const product = products.find((p) => p._id === productId);
    if (product) {
      const updatedCart = [...cart, product];
      setCart(updatedCart);
      localStorage.setItem("cart", JSON.stringify(updatedCart));
      alert(`Product ${productId} added to cart!`);
    }
  };

  return (
    <>
      <Navbar />
      <div
        className="cart-icon-container"
        onClick={() => navigate("/cart")}
        style={{
          position: "fixed",
          top: "20px",
          right: "20px",
          zIndex: "1000",
          cursor: "pointer",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          background: "#ffffff",
          padding: "10px",
          borderRadius: "50%",
          boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
        }}
      >
        <div
          className="cart-icon"
          style={{ fontSize: "24px", position: "relative" }}
        >
          🛒
          {cart.length > 0 && (
            <span
              className="cart-dot"
              style={{
                position: "absolute",
                top: "0",
                right: "0",
                width: "10px",
                height: "10px",
                backgroundColor: "red",
                borderRadius: "50%",
                border: "2px solid white",
              }}
            ></span>
          )}
        </div>
      </div>
      <div
        className={isBlurry ? "blurry" : ""}
        onClick={collapseAll} // Collapse cards when clicking outside
      >
        <div
          className="max-w-4xl mx-auto p-6"
          onClick={(e) => e.stopPropagation()}
        >
          {/* Prevent collapsing when clicking inside the container */}
          <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
            Product Catalog
          </h2>
          {error && (
            <div className="text-red-500 text-center mb-4">{error}</div>
          )}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {products.map((product, index) => (
              <div
                key={product._id}
                onClick={(e) => {
                  e.stopPropagation(); // Prevent collapse when clicking the card
                  toggleCard(index);
                }}
                className={`product-card ${product.expanded ? "expanded" : ""}`}
                style={{
                  backgroundColor: "#1f2937",
                  color: "#ffffff",
                  padding: "16px",
                  borderRadius: "8px",
                  boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                  transition: "transform 0.3s ease",
                }}
              >
                <img
                  src={product.img}
                  alt={product.name}
                  className="w-full h-40 object-cover rounded-md mb-4"
                  style={{ borderRadius: "8px" }}
                />
                <h3
                  className="text-lg font-semibold"
                  style={{ color: "#f3f4f6" }}
                >
                  {product.name}
                </h3>
                <p style={{ color: "#d1d5db" }}>Price: Rs.{product.price}</p>
                {product.expanded && (
                  <div className="card-details">
                    <p style={{ color: "#d1d5db" }}>
                      Category: {product.category}
                    </p>
                    <p style={{ color: "#d1d5db" }}>Stock: {product.stock}</p>
                    <div className="mt-2" style={{ color: "#e5e7eb" }}>
                      <strong>Description:</strong> {product.description}
                    </div>
                    <button
                      onClick={(e) => {
                        e.stopPropagation(); // Prevent triggering the card toggle
                        addToCart(product._id);
                      }}
                      className="mt-4 w-full py-2 bg-indigo-600 text-white font-medium rounded-md shadow hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-400 transition"
                    >
                      Add to Cart
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default UserPanel;
